void CncPositionTypes::resize(OpcUa_UInt32 length)
{
    if (length < m_noOfElements)
    {
        if (length != 0)
        {
            for (OpcUa_UInt32 i = length; i < m_noOfElements; i++)
            {
                OpcUaCnc_CncPositionType_Clear(&(m_data[i]));
            }

            OpcUaCnc_CncPositionType* newData = (OpcUaCnc_CncPositionType*)OpcUa_ReAlloc(m_data, length * sizeof(OpcUaCnc_CncPositionType));
            if (newData)
            {
                m_data = newData;
                m_noOfElements = length;
            }
        }
        else
        {
            clear();
        }
    }
    else if (length > m_noOfElements)
    {
        OpcUaCnc_CncPositionType* newData = (OpcUaCnc_CncPositionType*)OpcUa_ReAlloc(m_data, length * sizeof(OpcUaCnc_CncPositionType));
        if (newData)
        {
            for (OpcUa_UInt32 i = m_noOfElements; i < length; i++)
            {
                OpcUaCnc_CncPositionType_Initialize(&(newData[i]));
            }
            m_data = newData;
            m_noOfElements = length;
        }
    }
}

void CncPositionTypes::clear()
{
    if (m_data)
    {
        for (OpcUa_UInt32 i = 0; i < m_noOfElements; i++)
        {
            OpcUaCnc_CncPositionType_Clear(&(m_data[i]));
        }
        OpcUa_Free(m_data);
        m_data = (OpcUaCnc_CncPositionType*)OpcUa_Null;
    }
    m_noOfElements = 0;
}

