OpcUa_StatusCode CncPositionTypes::setCncPositionTypes(OpcUa_Variant &variant, OpcUa_Boolean bDetach)
{
    clear();

    OpcUa_StatusCode ret = OpcUa_BadTypeMismatch;
    if (variant.Datatype == OpcUaType_ExtensionObject &&
        variant.ArrayType == OpcUa_VariantArrayType_Array)
    {
        ret = OpcUa_Good;
        m_noOfElements = variant.Value.Array.Length >= 0 ? variant.Value.Array.Length : 0;
        if (m_noOfElements > 0 && variant.Value.Array.Value.ExtensionObjectArray != OpcUa_Null)
        {
            m_data = (OpcUaCnc_CncPositionType*)OpcUa_Alloc(m_noOfElements * sizeof(OpcUaCnc_CncPositionType));

            OpcUa_UInt32 i = 0;
            for (i = 0; i < m_noOfElements; i++)
            {
                OpcUaCnc_CncPositionType_Initialize(&m_data[i]);

                OpcUaCnc_CncPositionType *pValue = OPCUACNC_EXTENSIONOBJECT_GET_ENCODEABLE(CncPositionType, &variant.Value.Array.Value.ExtensionObjectArray[i]);
                if (pValue)
                {
                    if (bDetach != OpcUa_False)
                    {
                        m_data[i] = *pValue;
                        OpcUaCnc_CncPositionType_Initialize(pValue);
                    }
                    else
                    {
                        OpcUaCnc_CncPositionType_CopyTo(pValue, &m_data[i]);
                    }
                }
                else
                {
                    ret = OpcUa_BadTypeMismatch;
                    break;
                }
            }
            if (OpcUa_IsNotGood(ret))
            {
                for (OpcUa_UInt32 j = 0; j < i; j++)
                {
                    OpcUaCnc_CncPositionType_Clear(&m_data[j]);
                }
                OpcUa_Free(m_data);
                m_data = (OpcUaCnc_CncPositionType*)OpcUa_Null;
                m_noOfElements = 0;
            }
        }
        else
        {
            m_noOfElements = 0;
        }
    }
    return ret;
}

OpcUa_StatusCode CncPositionTypes::setCncPositionTypes(OpcUa_Int32 length, OpcUaCnc_CncPositionType* data)
{
    clear();

    if (length > 0 && data != OpcUa_Null)
    {
        m_noOfElements = length;
        m_data = (OpcUaCnc_CncPositionType*)OpcUa_Alloc(m_noOfElements * sizeof(OpcUaCnc_CncPositionType));
        for (OpcUa_UInt32 i = 0; i < m_noOfElements; i++)
        {
            OpcUaCnc_CncPositionType_CopyTo(&(data[i]), &(m_data[i]));
        }
    }
    return OpcUa_Good;
}

